        super(props)
        this.state = {
            FavDigimon:[],
            show: false,
            showData:false,
            name: '',
            img: '',
            level: '',
            index:''
        }
    }

    componentDidMount = async (req, res) => {
        try {
            const axiosGetUserData=await axios.get(`http://localhost:8000/fav`);
            const favoriteUserData = axiosGetUserData.data
            this.setState({
                FavDigimon: favoriteUserData,
                show:true
            })
        } catch {
           console.log('error')
        }
    }
    updateFavDigimon(idx) {
        this.setState({
            showData: true,
            name: this.state.FavDigimon[idx].name,
            img:this.state.FavDigimon[idx].img,
            level: this.state.FavDigimon[idx].level,
            index:idx
    })
}



    deleteFavDigimon = async (e, idx)=>{
        e.preventDefault();
        const DleteData=await axios.delete(`http://localhost:8000/del/${this.state.FavDigimon[idx]._id}`)
        this.setState({
            FavDigimon:DleteData.data
        })




    }

    changeName = (e) => {
        this.setState({
            name:e.target.name
        })
    }
    changeImg = (e) => {
        this.setState({
            img:e.target.img
        })
    }

    changeLevel = (e) => {
        this.setState({
                lev
            })
        }








    render() {
        return (
          <>
            {this.state.show &&
              this.state.FavDigimon.map((item, idx) => {
                return (
                  <>
                    <Card
                      key={idx}
                      style={{
                        width: "20rem",
                        margin: "15px",
                        display: "inline-block",
                        border: ".5px solid",
                        backgroundColor: "#B3C6F3",
                      }}
                    >
                      <Card.Img
                        variant="top"
                        src={item.img}
                        style={{ border: ".5px solid", backgroundColor: "red" }}
                      />
                      <Card.Body>
                        {/* <Card.Title>Card Title</Card.Title> */}
                        <Card.Text>{item.name}</Card.Text>
                        <Button style={{margin:'5px'}}
                          onClick={() => this.updateFavDigimon(idx)}
                          variant="primary"
                        >
                          update
                        </Button>
                        <Button
                          onClick={(e) => this.deleteFavDigimon(e,idx)}
                          variant="danger"
                        >
                          Delete
                        </Button>
                      </Card.Body>
                    </Card>
                  </>
                );
              })}
          </>
        );
    }
}

export default Favorite
