import React, { Component } from 'react'
import axios from 'axios'
import { Card, Button } from "react-bootstrap/";
export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      digimonData: [],
      show: false,
    };
  }

  componentDidMount = async () => {
    try {
      const alldataAPI = await axios.get(`http://localhost:8000/`);
      const finalGetData = alldataAPI.data;
      console.log(finalGetData);
      this.setState({
        digimonData: finalGetData,
        show: true,
      });
    } catch {
      console.log("Ooops don't have data");
    }
  };

  createDigimonFAV;

  render() {
    return (
      <>
        {this.state.show &&
          this.state.digimonData.map((item, idx) => {
            return (
              <>
                <Card style={{ width: "18rem" }}>
                  <Card.Img variant="top" src={item.img} />
                  <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                  </Card.Body>
                </Card>
              </>
            );
          })}
      </>
    );
  }
}

export default Home
